import axios from "axios";

export const initialState = {
  isLoggedIn: false,
  isLoggingIn: false,
  isLoggingOut: false,
  me: null,
  signUpData: {},
  loginData: {},
};

export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};

export const loginAction = (data) => {
  return (dispatch) => {
    dispatch(loginRequestAction());
    axios
      .post("/api/login")
      .then(() => {
        dispatch(loginSuccessAction());
      })
      .catch(() => {
        dispatch(loginFailureACtion());
      });
  };
};

export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data,
  };
};

export const loginSuccessAction = (data) => {
  return {
    type: "LOG_IN_SUCCESS",
    data,
  };
};

export const loginFailureACtion = (data) => {
  return {
    type: "LOG_IN_FAILURE",
    data,
  };
};

export const logoutRequestAction = (data) => {
  return {
    type: "LOG_OUT_REQUEST",
    data,
  };
};

export const logoutSuccessAction = (data) => {
  return {
    type: "LOG_OUT_SUCCESS",
    data,
  };
};

export const logoutFailuresAction = (data) => {
  return {
    type: "LOG_OUT_FAILURE",
    data,
  };
};

// export const loginAction = (data) => {
//   return (dispatch) => {
//     dispatch(loginRequestAction());
//     axios.post('/api/login').then((res)=>{
//       dispatch(loginSuccessAction(res.data));
//     }).catch((err)=>{
//       dispatch(loginFailureAction(err));
//     })
//     data,
//   };
// };

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggedIn: true,
        me: action.data,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: { ...action.data, nickname: "zerocho" },
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
        me: action.data,
      };
    case "LOG_OUT_REQUEST":
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    case "LOG_OUT_SUCCESS":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: true,
        me: action.data,
      };
    case "LOG_OUT_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: true,
        me: action.data,
      };
    default:
      return state;
  }
};

export default reducer;
