import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

export const logoutAction = (data) => {
  return {
    type: "LOG_OUT",
  };
};

export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};

// async action creater

//action creator
const changeNickname = (data) => {
  return {
    type: "CHANGE_NICKNAME",
    data,
  };
};

/*
const changeNickname = {
    type : 'CHANGE_NICKNAME',
    data : 'boogicho'
}

const changeNickname = {
    type : 'CHANGE_NICKNAME',
    data : 'neuezeal'
}
*/

// 리듀서 : (이전 상태, 액션) => 다음 상태  ... 축소 - 리듀스
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log("HYDRATE", action);
      return { ...state, ...action.payload };
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        },
      };
    default:
      return state;
  }
};

export default rootReducer;
