export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        Nicname: "제로초",
      },
      content: "첫번째 게시글",
      Images: [
        { src: "http://www.naver.com" },
        { src: "http://www.naver.com" },
        { src: "http://www.naver.com" },
      ],
      Contents: [{}],
    },
  ],
};

const ADD_POST = "ADD_POST";

export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: "더미데이터입니다.",
  User: {
    id: 1,
    nickname: "제로초",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST: {
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
      };
    }
    default:
      return state;
  }
};

export default reducer;
