import axios from "axios";

export const initialState = {
  isLoggedIn: false,
  isLoggingIn: false,
  isLoggingOut: false,
  me: null,
  signUpData: {},
  loginData: {},
};

export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};

export const loginAction = (data) => {
  console.log("abc");
  return {
    type: "LOG_IN",
    data,
  };
};

// export const loginAction = (data) => {
//   return (dispatch) => {
//     dispatch(loginRequestAction());
//     axios.post('/api/login').then((res)=>{
//       dispatch(loginSuccessAction(res.data));
//     }).catch((err)=>{
//       dispatch(loginFailureAction(err));
//     })
//     data,
//   };
// };

export const loginRequestAction = (data) => ({
  type: LOG_IN_REQUEST,
  data,
});

export const logoutRequestAction = () => ({
  type: LOG_OUT_REQUEST,
});

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggedIn: true,
        me: action.data,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: { ...action.data, nickname: "zerocho" },
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
        me: action.data,
      };
    case "LOG_OUT_REQUEST":
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    case "LOG_OUT_SUCCESS":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: true,
        me: action.data,
      };
    case "LOG_OUT_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: true,
        me: action.data,
      };
    default:
      return state;
  }
};

export default reducer;
